import React, { useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Video.css";
import Header from "../../components/header/Header";

const Stream = () => {
  const [comments, setComments] = useState([]);
  const [likes, setLikes] = useState(0);

  const handleComment = useCallback((event) => {
    event.preventDefault();
    const newComment = event.target.comment.value;
    setComments((prevComments) => [...prevComments, newComment]);
    event.target.comment.value = ""; // Clear the input field after adding comment
  }, []);

  const handleLike = useCallback(() => {
    setLikes((prevLikes) => prevLikes + 1);
  }, []);

  return (
    <div className="video">
      <Header />
      <div class="video-content">
        <img
          className="video-using-anima-plugin"
          alt=""
          src="/video-using-anima-plugin.svg"
        />
      </div>
      <div className="video-actions">
        <div className="like-container">
          <button className="like-button" onClick={handleLike}>
            <img src="/like.png" alt="Like" />
          </button>
          <span>{likes}</span>
        </div>
        <div className="comment-container">
          <div className="comment-section">
            <h3>Comments:</h3>
            <ul>
              {comments.map((comment, index) => (
                <li key={index}>{comment}</li>
              ))}
            </ul>
          </div>
          <form className="comment-form">
            <input type="text" className="comment" placeholder="Add a comment" />
          </form>
        </div>
      </div>
    </div>
  );
};

export default Stream;
